#!/usr/bin/env bash

sysctl net.ipv4.ip_forward=0
./nohype_settings 5
./4-core-reset linux.nohype 0.dtb 1.dtb 2.dtb 3.dtb 0x00000000 0x20000000 0x40000000 0x60000000
